"use client";

import { useState, useRef, useEffect } from "react";
import Sidebar from "@/components/Sidebar";
import ThoughtStream, { ThoughtStep } from "@/components/ThoughtStream";
import { Send, Sparkles, Database, FileText, Brain, FolderSearch, Globe, Terminal, CheckCircle2, AlertTriangle, Check, X, History, Plus, MessageSquare, Trash2 } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface Message {
    id: string;
    role: "user" | "assistant";
    content: string;
    timestamp: Date;
    tools?: { name: string; detail: string; icon: any; latency: number }[];
    pendingActions?: any[];
}

export default function ChatPage() {
    const [mounted, setMounted] = useState(false);
    const [messages, setMessages] = useState<Message[]>([
        {
            id: "initial",
            role: "assistant",
            content: "Welcome. Neural core active and synchronised. How can I assist you today?",
            timestamp: new Date()
        }
    ]);
    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [thoughtSteps, setThoughtSteps] = useState<ThoughtStep[]>([
        { id: 1, type: "thought", message: "Neural core active and waiting for instructions.", icon: Terminal, time: "just now" }
    ]);
    const [selectedModel, setSelectedModel] = useState<"gemini" | "ollama">("gemini");
    const [agentHistory, setAgentHistory] = useState<any[]>([]);

    // Session History State
    const [isHistoryOpen, setIsHistoryOpen] = useState(false);
    const [sessions, setSessions] = useState<{ id: string, title: string, updated_at: string }[]>([]);
    const [currentSessionId, setCurrentSessionId] = useState<string | null>(null);

    const scrollRef = useRef<HTMLDivElement>(null);

    const fetchSessions = async () => {
        try {
            const res = await fetch("http://localhost:8000/sessions");
            const data = await res.json();
            if (data.status === "success") {
                setSessions(data.sessions);
            }
        } catch (e) {
            console.error("Failed to fetch sessions:", e);
        }
    };

    const loadSession = async (sessionId: string) => {
        try {
            const res = await fetch(`http://localhost:8000/sessions/${sessionId}/messages`);
            const data = await res.json();
            if (data.status === "success") {
                const loadedMsgs = data.messages.map((m: any) => ({
                    id: m.id,
                    role: m.role,
                    content: m.content,
                    timestamp: new Date(m.created_at),
                    pendingActions: m.metadata?.pendingActions
                }));
                setMessages(loadedMsgs);
                setCurrentSessionId(sessionId);
                setAgentHistory([]); // Reset running local memory buffer
            }
        } catch (e) {
            console.error("Failed to load session:", e);
        }
    };

    const startNewSession = () => {
        setMessages([{
            id: Date.now().toString(),
            role: "assistant",
            content: "Welcome back! Ready for a new conversation.",
            timestamp: new Date()
        }]);
        setCurrentSessionId(null);
        setAgentHistory([]);
        setThoughtSteps([{ id: Date.now(), type: "thought", message: "Memory buffer cleared. Waiting...", icon: Terminal, time: "just now" }]);
    };

    const deleteSession = async (sessionId: string, e: React.MouseEvent) => {
        e.stopPropagation();
        try {
            await fetch(`http://localhost:8000/sessions/${sessionId}`, { method: "DELETE" });
            if (sessionId === currentSessionId) {
                startNewSession();
            }
            fetchSessions();
        } catch (error) {
            console.error("Failed to delete session:", error);
        }
    };

    useEffect(() => {
        setMounted(true);
        fetchSessions();
    }, []);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    const handleActionApproval = async (actionId: string, approved: boolean, messageId: string) => {
        try {
            const response = await fetch("http://localhost:8000/actions/approve", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ action_id: actionId, approved })
            });
            const data = await response.json();

            // Usuni??cie akcji z historii wiadomo??ci w UI (lub ukrycie jako zatwierdzone)
            setMessages(prev => prev.map(m => {
                if (m.id === messageId && m.pendingActions) {
                    return {
                        ...m,
                        pendingActions: m.pendingActions.filter(a => a.id !== actionId)
                    };
                }
                return m;
            }));

            // Ciche do????czenie loga do chatu jako nowy powr??t z informacj?? dla usera
            if (data.status === "success" && approved) {
                const sysMsg: Message = {
                    id: Date.now().toString(),
                    role: "assistant",
                    content: `[System] Action approved. ${data.message}`,
                    timestamp: new Date()
                };
                setMessages(prev => [...prev, sysMsg]);
            } else if (data.status === "success" && !approved) {
                const sysMsg: Message = {
                    id: Date.now().toString(),
                    role: "assistant",
                    content: `[System] Action rejected.`,
                    timestamp: new Date()
                };
                setMessages(prev => [...prev, sysMsg]);
            }
        } catch (error) {
            console.error("Failed to process approval", error);
        }
    };

    if (!mounted) return null;

    const handleSend = async () => {
        if (!input.trim() || isLoading) return;

        const userMessage: Message = {
            id: Date.now().toString(),
            role: "user",
            content: input,
            timestamp: new Date(),
        };

        setMessages((prev) => [...prev, userMessage]);
        setInput("");
        setIsLoading(true);
        setThoughtSteps([
            { id: Date.now(), type: "thought", message: "Analyzing user request for context...", icon: Terminal, time: "just now" }
        ]);

        try {
            const response = await fetch("http://localhost:8000/chat", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    message: input,
                    model: selectedModel,
                    session_id: currentSessionId,
                    message_history: agentHistory.length > 0 ? agentHistory : undefined
                }),
            });

            const data = await response.json();

            if (data.status === "success") {
                const usedTools: { name: string; detail: string; icon: any; latency: number }[] = [];
                const newThoughts: ThoughtStep[] = [];
                if (data.new_messages) {
                    data.new_messages.forEach((msg: any) => {
                        if (msg.parts) {
                            msg.parts.forEach((part: any, index: number) => {
                                if (part.part_kind === "tool-call") {
                                    let detail = "";
                                    let icon = Database;
                                    let messageStr = "";
                                    if (part.tool_name === "read_file" && part.args?.path) {
                                        detail = part.args.path;
                                        icon = FileText;
                                        messageStr = `Reading file: ${part.args.path}`;
                                    } else if (part.tool_name === "search_knowledge_base") {
                                        detail = "Knowledge Base";
                                        icon = Database;
                                        messageStr = `Searching knowledge base for '${part.args?.query || ""}'`;
                                    } else if (part.tool_name === "recall") {
                                        detail = "Vector Memory";
                                        icon = Brain;
                                        messageStr = `Recalling memories for '${part.args?.query || ""}'`;
                                    } else if (part.tool_name === "list_directory") {
                                        detail = "File System";
                                        icon = FolderSearch;
                                        messageStr = `Listing directory: ${part.args?.path || "."}`;
                                    } else if (part.tool_name === "web_search") {
                                        detail = "Tavily Web Search";
                                        icon = Globe;
                                        messageStr = `Web search: ${part.args?.query || ""}`;
                                    } else {
                                        detail = part.tool_name;
                                        messageStr = `Executing tool: ${part.tool_name}`;
                                    }

                                    if (!usedTools.find(t => t.name === part.tool_name && t.detail === detail)) {
                                        usedTools.push({
                                            name: part.tool_name,
                                            detail,
                                            icon,
                                            latency: Math.floor(Math.random() * 30 + 15) // mock latency 15-45ms
                                        });

                                        newThoughts.push({
                                            id: Date.now() + index,
                                            type: "tool",
                                            message: messageStr,
                                            icon: icon,
                                            time: "just now"
                                        });
                                    }
                                }
                            });
                        }
                    });
                }

                if (newThoughts.length > 0) {
                    setThoughtSteps(prev => [...prev, ...newThoughts]);
                }

                setThoughtSteps(prev => [...prev, {
                    id: Date.now() + 1000,
                    type: "complete",
                    message: "Response synthesized with high confidence",
                    icon: CheckCircle2,
                    time: "just now"
                }]);

                const assistantMessage: Message = {
                    id: (Date.now() + 1).toString(),
                    role: "assistant",
                    content: data.response,
                    timestamp: new Date(),
                    tools: usedTools.length > 0 ? usedTools : undefined,
                    pendingActions: data.pending_actions?.length > 0 ? data.pending_actions : undefined
                };
                setMessages((prev) => [...prev, assistantMessage]);
                if (data.new_messages) {
                    setAgentHistory((prev) => [...prev, ...data.new_messages]);
                }
                if (data.session_id && data.session_id !== currentSessionId) {
                    setCurrentSessionId(data.session_id);
                    fetchSessions();
                }
            } else {
                console.error("Agent error:", data.message);
            }
        } catch (error) {
            console.error("Failed to connect to Aether backend:", error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="flex h-screen w-full bg-[#1e1e1e] overflow-hidden font-sans text-foreground">

            <Sidebar />

            <main className="flex-1 min-w-0 flex relative overflow-hidden bg-[#1e1e1e]">

                {/* Chat Column */}
                <div className="flex-1 flex flex-col relative overflow-hidden">
                    {/* Chat Header ??? VSCode Style */}
                    <div className="px-5 py-4 border-b border-white/5 flex items-center justify-between shrink-0">
                        <div className="flex items-center gap-2">
                            <div className="w-2 h-2 rounded-full bg-purple-500 animate-pulse" />
                            <h3 className="text-sm font-bold text-white">Aether Agent</h3>
                        </div>
                        <div className="flex items-center gap-3 text-[10px] text-neutral-500 font-mono uppercase tracking-widest overflow-hidden">


                            {/* History Toggle */}
                            <button
                                onClick={() => setIsHistoryOpen(!isHistoryOpen)}
                                className={`flex items-center gap-1.5 px-3 h-7 rounded-sm border border-white/5 transition-all
                                    ${isHistoryOpen ? "bg-purple-500/20 shadow-[0_0_10px_rgba(168,85,247,0.2)] text-purple-400 font-bold border-purple-500/30" : "bg-black/40 text-neutral-400 hover:text-white hover:bg-white/10"}
                                `}
                            >
                                <History className="w-3.5 h-3.5" />
                                <span className="hidden sm:inline">HISTORY Logs</span>
                            </button>

                            <span className="hidden sm:inline">???</span>
                            <div className="flex items-center gap-1.5 shrink-0">
                                <span className={`w-1.5 h-1.5 rounded-full ${selectedModel === 'gemini' ? 'bg-purple-500/50' : 'bg-blue-500/50'}`} />
                                <span>{selectedModel === 'gemini' ? 'Core Online' : 'Local Edge'}</span>
                            </div>
                            <span className="hidden md:inline">???</span>
                            <span className="hidden md:inline">Latency: ~12ms</span>
                        </div>
                    </div>

                    {/* Message Container */}
                    <div
                        ref={scrollRef}
                        className="flex-1 overflow-y-auto p-4 md:p-6 space-y-6 scrollbar-thin scrollbar-thumb-white/5 scrollbar-track-transparent"
                    >
                        {messages.length === 0 ? (
                            <div className="h-full flex flex-col items-center justify-center text-center space-y-4 opacity-30">
                                <Sparkles className="w-10 h-10 text-purple-400" />
                                <div className="space-y-1.5">
                                    <h3 className="text-lg font-light text-white">Neural Core Ready</h3>
                                    <p className="text-xs text-neutral-500 max-w-xs mx-auto">Input command or query to begin processing.</p>
                                </div>
                            </div>
                        ) : (
                            <AnimatePresence>
                                {messages.map((msg) => (
                                    <motion.div
                                        key={msg.id}
                                        initial={{ opacity: 0, scale: 0.98 }}
                                        animate={{ opacity: 1, scale: 1 }}
                                        className={`flex gap-3 ${msg.role === "user" ? "justify-end" : "justify-start"}`}
                                    >
                                        {msg.role === "assistant" && (
                                            <div className="w-7 h-7 rounded-lg bg-purple-500/20 border border-purple-500/30 flex items-center justify-center shrink-0">
                                                <span className="text-[8px] font-bold text-purple-400">AI</span>
                                            </div>
                                        )}

                                        <div className={`max-w-[75%] px-4 py-3 ${msg.role === "user"
                                            ? "bg-purple-500/10 border border-purple-500/20 rounded-xl rounded-tr-sm"
                                            : "bg-white/[0.03] border border-white/5 rounded-xl rounded-tl-sm"
                                            }`}>
                                            <div className="text-sm text-neutral-300 leading-relaxed whitespace-pre-wrap">
                                                {msg.content.split(/(\d+ new memories|\d+ recently indexed documents|Project architecture overview|Authentication system design|API endpoint documentation)/).map((part, i) => {
                                                    if (part === "3 new memories") return <span key={i} className="text-purple-400 font-medium">{part}</span>;
                                                    if (part === "3 recently indexed documents") return <span key={i} className="text-blue-400 font-medium">{part}</span>;
                                                    if (["Project architecture overview", "Authentication system design", "API endpoint documentation"].includes(part)) {
                                                        return <span key={i} className="flex items-center gap-2 mt-1.5 first:mt-2"><span className="w-1 h-1 rounded-full bg-blue-400" />{part}</span>;
                                                    }
                                                    return part;
                                                })}
                                            </div>
                                            <p className="text-[10px] text-neutral-600 font-mono mt-2 uppercase">
                                                {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                            </p>

                                            {/* Tool Calls Visualization */}
                                            {msg.tools && msg.tools.length > 0 && (
                                                <div className="mt-4 pt-3 border-t border-white/5 flex flex-wrap gap-2">
                                                    {msg.tools.map((tool, idx) => {
                                                        const Icon = tool.icon;
                                                        return (
                                                            <div key={idx} className="flex flex-col gap-1.5 bg-black/40 border border-white/5 rounded-lg px-3 py-2 min-w-[140px]">
                                                                <div className="flex items-center gap-2 text-[10px] text-neutral-400 font-mono flex-1">
                                                                    <Icon className="w-3 h-3 text-purple-400" />
                                                                    <span>{tool.name}</span>
                                                                </div>
                                                                <div className="text-xs text-neutral-200 truncate max-w-[200px]">
                                                                    {tool.detail}
                                                                </div>
                                                                <div className="flex items-center gap-1.5 text-[9px] text-neutral-600 font-mono mt-0.5">
                                                                    <div className="w-1 h-1 rounded-full bg-green-500/50" />
                                                                    lat: {tool.latency}ms
                                                                </div>
                                                            </div>
                                                        );
                                                    })}
                                                </div>
                                            )}

                                            {/* Human-in-the-Loop Actions */}
                                            {msg.pendingActions && msg.pendingActions.length > 0 && (
                                                <div className="mt-4 space-y-3">
                                                    {msg.pendingActions.map((action) => (
                                                        <div key={action.id} className="border border-orange-500/30 bg-orange-500/5 rounded-xl p-4 shadow-lg shadow-orange-500/5">
                                                            <div className="flex items-center gap-2 text-orange-400 font-bold mb-2">
                                                                <AlertTriangle className="w-4 h-4" />
                                                                <span>Action Required: {action.type === "write_file" ? "File Modification" : action.type}</span>
                                                            </div>
                                                            <p className="text-sm text-neutral-300 mb-3 font-medium">
                                                                The agent wants to modify <code className="bg-black/40 px-1.5 py-0.5 rounded text-orange-300 font-mono text-xs">{action.display_path}</code>.
                                                            </p>

                                                            <div className="bg-[#1e1e1e] border border-white/5 rounded-lg p-3 text-[11px] text-neutral-400 font-mono mb-4 max-h-40 overflow-y-auto scrollbar-thin scrollbar-thumb-white/10">
                                                                <pre>{action.content}</pre>
                                                            </div>

                                                            <div className="flex gap-3">
                                                                <button
                                                                    onClick={() => handleActionApproval(action.id, true, msg.id)}
                                                                    className="flex-1 flex items-center justify-center gap-2 py-2.5 bg-orange-500 hover:bg-orange-600 text-white rounded-lg text-xs font-bold transition-all active:scale-[0.98]"
                                                                >
                                                                    <Check className="w-4 h-4" />
                                                                    Approve & Execute
                                                                </button>
                                                                <button
                                                                    onClick={() => handleActionApproval(action.id, false, msg.id)}
                                                                    className="flex-1 flex items-center justify-center gap-2 py-2.5 bg-white/5 hover:bg-white/10 text-neutral-300 rounded-lg text-xs font-bold transition-all active:scale-[0.98]"
                                                                >
                                                                    <X className="w-4 h-4" />
                                                                    Reject
                                                                </button>
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            )}
                                        </div>

                                        {msg.role === "user" && (
                                            <div className="w-7 h-7 rounded-lg bg-white/10 border border-white/10 flex items-center justify-center shrink-0">
                                                <span className="text-[8px] font-bold text-white/60">TK</span>
                                            </div>
                                        )}
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        )}

                        {isLoading && (
                            <motion.div
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                className="flex gap-3"
                            >
                                <div className="w-7 h-7 rounded-lg bg-purple-500/20 border border-purple-500/30 flex items-center justify-center shrink-0 animate-pulse">
                                    <span className="text-[8px] font-bold text-purple-400">AI</span>
                                </div>
                                <div className="bg-white/[0.03] border border-white/5 rounded-xl rounded-tl-sm px-4 py-3 flex items-center gap-2">
                                    <div className="w-1.5 h-1.5 rounded-full bg-purple-400 animate-bounce" />
                                    <div className="w-1.5 h-1.5 rounded-full bg-purple-400 animate-bounce [animation-delay:0.2s]" />
                                    <div className="w-1.5 h-1.5 rounded-full bg-purple-400 animate-bounce [animation-delay:0.4s]" />
                                </div>
                            </motion.div>
                        )}
                    </div>

                    {/* Input Area ??? VSCode Style */}
                    <div className="px-4 md:px-6 py-4 shrink-0 bg-transparent relative">
                        <div className="absolute inset-0 bg-gradient-to-t from-[#1e1e1e] via-[#1e1e1e] to-transparent pointer-events-none -top-10" />
                        <div className="flex items-center gap-2 bg-[#252526] border border-white/10 rounded-xl px-4 py-3 focus-within:border-purple-500/50 transition-all duration-300 relative z-10 shadow-lg shadow-black/20">
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                onKeyDown={(e) => e.key === "Enter" && handleSend()}
                                placeholder="Type a message..."
                                className="flex-1 bg-transparent text-[#cccccc] text-sm placeholder:text-[#858585] focus:outline-none"
                            />
                            <button
                                onClick={handleSend}
                                disabled={isLoading || !input.trim()}
                                className="p-2 rounded-lg bg-purple-500/20 hover:bg-purple-500/30 text-purple-400 transition-all disabled:opacity-30 active:scale-95"
                            >
                                <Send className="w-4 h-4" />
                            </button>
                        </div>
                    </div>

                    {/* Bottom Bar ??? System Status */}
                    <div className="flex items-center justify-between px-6 py-4 text-[10px] font-mono text-neutral-600 uppercase tracking-widest shrink-0 w-full">
                        <div className="flex items-center gap-3">
                            <div className="flex items-center gap-1.5">
                                <div className="w-1.5 h-1.5 rounded-full bg-green-500/30 animate-pulse" />
                                <span className="text-green-500/40">Local Instance</span>
                            </div>
                            <span className="text-neutral-800">???</span>
                            <span>Aether v0.1.0</span>
                        </div>
                        <div className="hidden md:block">
                            System Ready // <span className="text-neutral-700">Aether Workspace</span>
                        </div>
                    </div>
                </div >

                {/* Right History Drawer */}
                <AnimatePresence>
                    {
                        isHistoryOpen && (
                            <motion.div
                                initial={{ width: 0, opacity: 0 }}
                                animate={{ width: 320, opacity: 1 }}
                                exit={{ width: 0, opacity: 0 }}
                                className="bg-[#181818] border-l border-[#303030] flex flex-col shrink-0 overflow-hidden font-mono z-20 h-full"
                            >
                                <div className="px-5 py-4 border-b border-white/5 flex items-center justify-between shrink-0 bg-[#1e1e1e]">
                                    <h3 className="text-xs font-bold text-white uppercase tracking-widest flex items-center gap-2">
                                        <Database className="w-4 h-4 text-purple-500" />
                                        Chronicles
                                    </h3>
                                    <button
                                        onClick={startNewSession}
                                        className="p-1.5 rounded bg-white/5 hover:bg-white/10 text-white transition-colors"
                                    >
                                        <Plus className="w-3.5 h-3.5" />
                                    </button>
                                </div>

                                <div className="flex-1 overflow-y-auto p-3 space-y-1 scrollbar-thin scrollbar-thumb-white/10">
                                    {sessions.length === 0 ? (
                                        <div className="p-4 text-center text-xs text-neutral-600 italic">No previous sessions found.</div>
                                    ) : (
                                        sessions.map(s => (
                                            <div key={s.id} className="group flex items-center gap-1 relative">
                                                <button
                                                    onClick={() => loadSession(s.id)}
                                                    className={`w-full text-left px-3 py-3 rounded-lg flex flex-col gap-1 transition-all
                                                    ${s.id === currentSessionId
                                                            ? "bg-purple-500/10 border border-purple-500/20"
                                                            : "bg-transparent border border-transparent hover:bg-white/[0.02]"
                                                        }
                                                `}
                                                >
                                                    <div className="flex items-start gap-2 max-w-[90%]">
                                                        <MessageSquare className={`w-3.5 h-3.5 mt-0.5 shrink-0 ${s.id === currentSessionId ? "text-purple-400" : "text-neutral-600"}`} />
                                                        <span className={`text-[11px] font-sans truncate font-medium ${s.id === currentSessionId ? "text-purple-200" : "text-neutral-400"}`}>
                                                            {s.title}
                                                        </span>
                                                    </div>
                                                    <span className="text-[9px] text-neutral-600 ml-5.5">
                                                        {new Date(s.updated_at).toLocaleString()}
                                                    </span>
                                                </button>

                                                <button
                                                    onClick={(e) => deleteSession(s.id, e)}
                                                    className="absolute right-2 top-1/2 -translate-y-1/2 opacity-0 group-hover:opacity-100 p-1.5 rounded-md text-neutral-500 hover:text-red-400 hover:bg-red-500/10 transition-all flex items-center justify-center"
                                                    title="Delete Session"
                                                >
                                                    <Trash2 className="w-3.5 h-3.5" />
                                                </button>
                                            </div>
                                        ))
                                    )}
                                </div>
                            </motion.div>
                        )
                    }
                </AnimatePresence>
            </main>

            <ThoughtStream steps={thoughtSteps} />
        </div>
    );
}

"use client";

import Sidebar from "@/components/Sidebar";
import { Zap, Shield, Cpu, Clock, Activity, MessageSquare, Send, Brain, Database, Globe } from "lucide-react";
import Link from "next/link";
import { useState, useEffect } from "react";
import { motion } from "framer-motion";

interface DashboardMessage {
  id: string;
  role: "user" | "assistant";
  content: string;
  isInitial?: boolean;
  extra?: string[];
  sources?: string[];
}

export default function Home() {
  const [input, setInput] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [messages, setMessages] = useState<DashboardMessage[]>([]);

  const [stats, setStats] = useState({ memories: 0, documents: 0, reliability: 100, sessions: 0 });
  const [activities, setActivities] = useState<any[]>([]);
  const [modelName, setModelName] = useState("Loading...");

  useEffect(() => {
    // Fetch stats
    fetch("http://localhost:8000/stats")
      .then(res => res.json())
      .then(data => {
        if (data.status === "success") {
          setStats({
            memories: data.stats.memories_count,
            documents: data.stats.documents_count,
            reliability: data.stats.reliability || 100,
            sessions: data.stats.sessions_count || 0
          });
        }
      })
      .catch(err => console.error("Stats error:", err));

    const fetchConfig = () => {
      fetch("http://localhost:8000/config")
        .then(res => res.json())
        .then(data => {
          if (data.status === "success") {
            const raw = data.config.MODEL_OVERRIDE || "gemini-3.1-pro";
            const formatted = raw
              .replace("ollama:", "")
              .replace(/-/g, " ")
              .replace(/\b\w/g, (c: string) => c.toUpperCase());
            setModelName(formatted);
          }
        })
        .catch(() => setModelName("Aether Core"));
    };

    fetchConfig();
    window.addEventListener("configUpdated", fetchConfig);

    // Fetch recent activity
    fetch("http://localhost:8000/recent_activity")
      .then(res => res.json())
      .then(data => {
        setActivities(data.activities);
      })
      .catch(err => console.error("Activity error:", err));

    // Fetch Morning Brief (Night Cycle Output)
    fetch("http://localhost:8000/system/morning-brief")
      .then(res => res.json())
      .then(data => {
        if (data.status === "success" && data.report) {
          setMessages([{
            id: "initial-" + Date.now(),
            role: "assistant",
            content: data.report.brief,
            extra: data.report.points,
            sources: ["aether.sleep_cycle", "system.logs"],
            isInitial: true
          }]);
        }
      })
      .catch(err => console.error("Morning Brief fetch error:", err));

    return () => window.removeEventListener("configUpdated", fetchConfig);
  }, []);

  const triggerSleepCycle = async () => {
    setIsProcessing(true);
    try {
      setMessages(prev => [...prev, {
        id: Date.now().toString(),
        role: "user",
        content: "EXECUTE SYSTEM OVERRIDE: run_sleep_cycle()"
      }]);
      const res = await fetch("http://localhost:8000/system/sleep-cycle", { method: "POST" });
      const data = await res.json();
      if (data.status === "success" && data.report) {
        setMessages(prev => [...prev, {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: data.report.brief,
          extra: data.report.points,
          sources: ["aether.sleep_cycle", "analysis.engine"]
        }]);
      } else {
        throw new Error(data.message || "Unknown error");
      }
    } catch (err: any) {
      setMessages(prev => [...prev, {
        id: Date.now().toString(),
        role: "assistant",
        content: `Error running night cycle: ${err.message}`
      }]);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMsg: DashboardMessage = {
      id: Date.now().toString(),
      role: "user",
      content: input,
    };

    setMessages(prev => [...prev, userMsg]);
    const currentInput = input;
    setInput("");
    setIsProcessing(true);

    try {
      const response = await fetch("http://localhost:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: currentInput }),
      });

      const data = await response.json();

      if (data.status === "success") {
        const aiMsg: DashboardMessage = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: data.response,
        };
        setMessages(prev => [...prev, aiMsg]);
      } else {
        const errorMsg: DashboardMessage = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: `Error: ${data.message}`,
        };
        setMessages(prev => [...prev, errorMsg]);
      }
    } catch (err) {
      console.error("Chat error:", err);
      const errorMsg: DashboardMessage = {
        id: Date.now().toString(),
        role: "assistant",
        content: "Error connecting to backend.",
      };
      setMessages(prev => [...prev, errorMsg]);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="flex h-screen w-full bg-background overflow-hidden font-sans text-foreground">

      <Sidebar />

      <main className="flex-1 flex min-w-0 relative overflow-hidden bg-background">
        <div className="flex-1 flex flex-col min-w-0 relative z-10 p-6 gap-4">

          {/* System Info Strip */}
          <motion.div
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex items-center gap-4 px-5 py-3 bg-[#181818] border border-[#303030] rounded-xl shrink-0"
          >
            <div className="flex items-center gap-1.5">
              <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
              <span className="text-[11px] font-mono text-green-400 uppercase">Online</span>
            </div>
            <span className="text-neutral-700">|</span>
            <div className="flex items-center gap-4 text-[11px] font-mono text-neutral-400">
              <span><Shield className="w-3 h-3 inline mr-1 text-green-400/60" />Memories: {stats.memories}</span>
              <span className="text-neutral-700">???</span>
              <span><Database className="w-3 h-3 inline mr-1 text-blue-400/60" />Docs: {stats.documents}</span>
              <span className="text-neutral-700">???</span>
              <span><MessageSquare className="w-3 h-3 inline mr-1 text-cyan-400/60" />Sessions: {stats.sessions}</span>
              <span className="text-neutral-700">???</span>
              <span><Activity className="w-3 h-3 inline mr-1 text-purple-400/60" />{stats.reliability}%</span>
            </div>
            <span className="text-neutral-700">|</span>
            <span className="text-[11px] font-mono text-neutral-500">{modelName}</span>
          </motion.div>


          {/* Row 3: Chat + Activity */}
          <div className="flex-1 grid grid-cols-12 gap-4 min-h-0 min-w-0">

            {/* Left: Command Center / Morning Brief (Terminal Style) */}
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.3 }}
              className="col-span-8 bg-[#1e1e1e] border border-[#303030] rounded-2xl flex flex-col overflow-hidden shadow-2xl"
            >
              {/* Terminal Title Bar */}
              <div className="flex items-center gap-2 px-4 py-3 border-b border-white/5 bg-white/[0.02] shrink-0">
                <div className="flex gap-1.5">
                  <div className="w-2.5 h-2.5 rounded-full bg-red-500/30 border border-red-500/50" />
                  <div className="w-2.5 h-2.5 rounded-full bg-yellow-500/30 border border-yellow-500/50" />
                  <div className="w-2.5 h-2.5 rounded-full bg-green-500/30 border border-green-500/50" />
                </div>
                <span className="ml-2 text-[10px] text-neutral-500 font-mono uppercase tracking-widest">aether ??? session_live</span>
                <div className="ml-auto flex items-center gap-1.5">
                  <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
                  <span className="text-[9px] text-green-500/70 font-mono font-bold tracking-tighter">CONNECTED</span>
                  <button
                    onClick={triggerSleepCycle}
                    disabled={isProcessing}
                    className="ml-3 text-[9px] bg-red-500/10 hover:bg-red-500/20 text-red-400 border border-red-500/20 px-2 py-0.5 rounded transition-colors font-mono disabled:opacity-50"
                  >
                    FORCE SLEEP CYCLE
                  </button>
                  <Link href="/chat" className="ml-2 text-[9px] bg-white/5 hover:bg-white/10 border border-white/10 px-2 py-0.5 rounded transition-colors text-neutral-400 font-mono">
                    OPEN TECHNICAL CHAT
                  </Link>
                </div>
              </div>

              {/* Terminal Content */}
              <div className="flex-1 overflow-y-auto p-5 space-y-6 font-mono text-[13px] leading-relaxed scrollbar-thin scrollbar-thumb-white/5 scrollbar-track-transparent">
                {messages.map((msg) => (
                  <div key={msg.id} className="space-y-4">
                    {msg.role === "user" ? (
                      <div className="flex items-start gap-2">
                        <span className="text-purple-400 font-bold shrink-0">user@local</span>
                        <span className="text-neutral-600">~</span>
                        <span className="text-neutral-500">$</span>
                        <span className="text-neutral-200 ml-1">{msg.content}</span>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        {/* Status lines (only for AI) */}
                        <div className="space-y-1 opacity-70">
                          <div className="text-blue-400/80">
                            <span>[aether]</span> <span className="text-neutral-500 italic">Evaluating system context & logs...</span>
                          </div>
                          <div className="flex items-center gap-2 text-[11px] text-neutral-600">
                            <span className="text-blue-400/50">[aether]</span>
                            <span className="text-green-400/60">Report synthesized</span>
                            <span>???</span>
                            <span>sys.time: {msg.isInitial ? "07:12:00" : "now"}</span>
                          </div>
                        </div>

                        {/* Main Response Box */}
                        <div className="bg-white/[0.02] border border-white/5 rounded-xl p-4 space-y-3">
                          <div className="flex items-center gap-2 text-[10px] text-green-400/70 font-bold uppercase tracking-widest border-b border-white/5 pb-2 mb-2">
                            <div className="w-1.5 h-1.5 rounded-full bg-green-500" />
                            <span>Aether Morning Brief / Terminal Return</span>
                          </div>
                          <div className="text-neutral-300 space-y-3">
                            <p>{msg.content}</p>
                            {msg.extra && (
                              <ul className="space-y-1 text-neutral-400">
                                {msg.extra.map((item, idx) => (
                                  <li key={idx} className="flex items-start gap-2.5">
                                    <span className="text-purple-600 mt-1">???</span>
                                    <span>{item}</span>
                                  </li>
                                ))}
                              </ul>
                            )}
                            {msg.sources && (
                              <div className="pt-2 flex flex-wrap gap-2 text-[10px] text-neutral-600">
                                {msg.sources.map((src, idx) => (
                                  <span key={idx} className="px-1.5 py-0.5 border border-white/5 rounded">{src}</span>
                                ))}
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                ))}

                {isProcessing && (
                  <div className="space-y-2 animate-pulse">
                    <div className="flex items-center gap-2">
                      <span className="text-purple-400 font-bold">user@local</span>
                      <span className="text-neutral-600">~</span>
                      <span className="text-neutral-500">$</span>
                      <div className="w-2 h-4 bg-purple-400/50" />
                    </div>
                    <div className="text-[11px] text-neutral-600 font-mono italic">
                      [aether] system.routing_neural_request...
                    </div>
                  </div>
                )}
              </div>

              {/* Terminal Input Area */}
              <div className="px-4 py-3 border-t border-[#303030] bg-[#1e1e1e] shrink-0">
                <div className="flex items-center gap-2 bg-[#3c3c3c]/30 border border-[#3c3c3c] rounded-lg px-4 py-2 focus-within:border-[#007acc]/50 transition-all bg-[#252526]">
                  <span className="text-purple-400/50 font-mono text-[10px] font-bold">AETHER_CMD:</span>
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Execute system command or run task..."
                    className="flex-1 bg-transparent text-[#cccccc] font-mono text-sm placeholder:text-[#858585] focus:outline-none"
                    onKeyDown={(e) => {
                      if (e.key === "Enter") handleSend();
                    }}
                  />
                  <button
                    onClick={handleSend}
                    disabled={isProcessing}
                    className="p-1 rounded bg-purple-500/10 hover:bg-purple-500/20 text-purple-400/60 hover:text-purple-400 transition-colors disabled:opacity-20"
                  >
                    <Send className="w-3.5 h-3.5" />
                  </button>
                </div>
              </div>
            </motion.div>

            {/* Right: Recent Activity */}
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.35 }}
              className="col-span-4 bg-[#252526] border border-[#303030] rounded-2xl flex flex-col overflow-hidden"
            >
              <div className="px-5 py-3 border-b border-white/5 flex items-center justify-between shrink-0">
                <h3 className="text-sm font-bold text-white">Recent Activity</h3>
                <Link href="/logs" className="text-[10px] text-purple-400/60 hover:text-purple-400 transition-colors font-mono">
                  View All ???
                </Link>
              </div>
              <div className="flex-1 overflow-y-auto divide-y divide-white/5">
                {activities.map((activity, i) => {
                  let Icon = Activity;
                  if (activity.icon === "Brain") Icon = Brain;
                  if (activity.icon === "MessageSquare") Icon = MessageSquare;

                  return (
                    <motion.div
                      key={i}
                      initial={{ opacity: 0, x: 10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.4 + i * 0.05 }}
                      className="px-5 py-3 flex items-start gap-3 hover:bg-white/[0.02] transition-colors cursor-default"
                    >
                      <Icon className={`w-3.5 h-3.5 ${activity.color} shrink-0 mt-0.5`} />
                      <div className="flex-1 min-w-0">
                        <span className="text-xs text-neutral-300 leading-relaxed block truncate">{activity.text}</span>
                        <span className="text-[10px] text-neutral-600 font-mono">{activity.time}</span>
                      </div>
                    </motion.div>
                  )
                })}
                {activities.length === 0 && (
                  <div className="px-5 py-8 text-center text-xs text-neutral-500 italic">No recent activity detected.</div>
                )}
              </div>
            </motion.div>
          </div>

          {/* Bottom Bar */}
          <div className="flex items-center justify-between px-2 text-[10px] font-mono text-neutral-600 uppercase tracking-wider shrink-0">
            <div className="flex items-center gap-3">
              <div className="flex items-center gap-1.5">
                <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
                <span className="text-green-400/60">Online</span>
              </div>
              <span className="text-neutral-700">???</span>
              <span>Command Center</span>
            </div>
            <span>Aether v0.1.0-alpha</span>
          </div>

        </div>

      </main>
    </div>
  );
}

"use client";

import Sidebar from "@/components/Sidebar";
import { Search, FileText, Upload, Filter, ExternalLink, Globe, Code, Loader2, Trash, Zap } from "lucide-react";
import { motion } from "framer-motion";
import { useState, useRef, useEffect } from "react";
import ConfirmationModal from "@/components/modals/ConfirmationModal";
import NotificationModal from "@/components/modals/NotificationModal";

export default function KnowledgeBase() {
    const [items, setItems] = useState<any[]>([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [isUploading, setIsUploading] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const fileInputRef = useRef<HTMLInputElement>(null);

    // Modal States
    const [deleteModal, setDeleteModal] = useState<{ isOpen: boolean; id: string | number | null; filename: string | null }>({
        isOpen: false,
        id: null,
        filename: null
    });
    const [notification, setNotification] = useState<{ isOpen: boolean; title: string; message: string; type: "success" | "error" }>({
        isOpen: false,
        title: "",
        message: "",
        type: "success"
    });

    // Filtered items based on search query
    const filteredItems = items.filter(item =>
        item.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Fetch documents on mount
    const fetchDocuments = async () => {
        setIsLoading(true);
        try {
            const res = await fetch("http://localhost:8000/knowledge");
            const data = await res.json();
            if (data.status === "success") {
                // Map DB documents to UI format
                const dbItems = data.documents.map((doc: any, index: number) => ({
                    id: `db-${index}-${doc.filename}`,
                    type: doc.filename.endsWith('.py') || doc.filename.endsWith('.js') || doc.filename.endsWith('.ts') ? 'code' : 'doc',
                    title: doc.filename,
                    added: doc.status === 'indexed' ? 'READY' : 'ON_DISK',
                    size: doc.size || "Unknown",
                    icon: doc.filename.endsWith('.py') || doc.filename.endsWith('.js') || doc.filename.endsWith('.ts') ? Code : FileText,
                    lines: doc.metadata?.total_chunks || 0
                }));

                setItems(dbItems);
            }
        } catch (err) {
            console.error("Failed to fetch documents:", err);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchDocuments();
    }, []);

    const handleFileClick = () => {
        fileInputRef.current?.click();
    };

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        setIsUploading(true);
        const formData = new FormData();
        formData.append("file", file);

        try {
            const res = await fetch("http://localhost:8000/ingest", {
                method: "POST",
                body: formData,
            });
            const data = await res.json();

            if (data.status === "success") {
                // Refresh list from DB
                await fetchDocuments();

                setNotification({
                    isOpen: true,
                    title: "FILE INDEXED",
                    message: `Successfully processed and indexed "${file.name}".`,
                    type: "success"
                });
            } else {
                setNotification({
                    isOpen: true,
                    title: "UPLOAD FAILED",
                    message: data.message || "Unknown error occurred.",
                    type: "error"
                });
            }
        } catch (err) {
            console.error("Upload error:", err);
            setNotification({
                isOpen: true,
                title: "CONNECTION ERROR",
                message: "Could not connect to the backend server.",
                type: "error"
            });
        } finally {
            setIsUploading(false);
            if (fileInputRef.current) fileInputRef.current.value = "";
        }
    };

    const initiateDelete = (e: React.MouseEvent, id: number, filename: string) => {
        e.stopPropagation();
        setDeleteModal({ isOpen: true, id, filename });
    };

    const confirmDelete = async () => {
        if (!deleteModal.filename || deleteModal.id === null) return;

        try {
            const res = await fetch(`http://localhost:8000/knowledge/${deleteModal.filename}`, {
                method: "DELETE",
            });
            const data = await res.json();

            if (data.status === "success") {
                setItems(prev => prev.filter(item => item.id !== deleteModal.id));
                setNotification({
                    isOpen: true,
                    title: "SOURCE DELETED",
                    message: `"${deleteModal.filename}" has been removed from the knowledge base.`,
                    type: "success"
                });
            } else {
                setNotification({
                    isOpen: true,
                    title: "DELETE FAILED",
                    message: data.message,
                    type: "error"
                });
            }
        } catch (err) {
            console.error("Delete error:", err);
            setNotification({
                isOpen: true,
                title: "CONNECTION ERROR",
                message: "Could not connect to the backend server.",
                type: "error"
            });
        }
    };

    const handleIndex = async (e: React.MouseEvent, filename: string) => {
        e.stopPropagation();
        setIsLoading(true);
        try {
            const res = await fetch(`http://localhost:8000/knowledge/index/${filename}`, {
                method: "POST",
            });
            const data = await res.json();
            if (data.status === "success") {
                await fetchDocuments();
                setNotification({
                    isOpen: true,
                    title: "INDEXING_COMPLETE",
                    message: `"${filename}" is now synchronized with Aether's neural core.`,
                    type: "success"
                });
            } else {
                setNotification({
                    isOpen: true,
                    title: "INDEXING_FAILED",
                    message: data.message,
                    type: "error"
                });
            }
        } catch (err) {
            console.error("Indexing error:", err);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="flex h-screen w-full bg-[#1e1e1e] overflow-hidden font-sans text-foreground">

            <Sidebar />

            <main className="flex-1 min-w-0 flex flex-col relative overflow-hidden z-10">

                {/* Header ??? Aligned with Dashboard Style */}
                <div className="px-6 py-4 border-b border-[#303030] flex items-center justify-between bg-[#181818] shrink-0">
                    <div className="flex items-center gap-3">
                        <div className="w-2 h-2 rounded-full bg-purple-500 animate-pulse" />
                        <div>
                            <h1 className="text-sm font-bold tracking-wider text-white uppercase">Knowledge Base</h1>
                            <div className="flex items-center gap-2 text-[10px] text-neutral-500 font-mono">
                                <span>SYSTEM.KNOWLEDGE_CORE</span>
                                <span className="text-neutral-700">|</span>
                                <span>{items.length} SOURCES ACTIVE</span>
                            </div>
                        </div>
                    </div>
                    <div className="flex gap-2">
                        <input
                            type="file"
                            ref={fileInputRef}
                            className="hidden"
                            onChange={handleFileChange}
                            accept=".md,.txt,.pdf"
                        />
                        <button className="px-3 py-1.5 rounded-lg bg-white/5 hover:bg-white/10 border border-white/10 text-[11px] font-mono transition-colors flex items-center gap-2 text-neutral-400">
                            <Filter className="w-3.5 h-3.5" /> FILTER
                        </button>
                        <button
                            onClick={handleFileClick}
                            disabled={isUploading}
                            className="px-3 py-1.5 rounded-lg bg-purple-500/20 hover:bg-purple-500/30 border border-purple-500/30 text-[11px] font-mono font-bold text-purple-400 transition-colors flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            {isUploading ? <Loader2 className="w-3.5 h-3.5 animate-spin" /> : <Upload className="w-3.5 h-3.5" />}
                            {isUploading ? "INDEXING..." : "ADD_SOURCE"}
                        </button>
                    </div>
                </div>

                {/* Main Content Area */}
                <div className="flex-1 overflow-y-auto p-6 space-y-6 scrollbar-thin scrollbar-thumb-white/5 scrollbar-track-transparent">

                    {/* Search Bar ??? VSCode Style */}
                    <div className="relative max-w-2xl bg-[#252526] border border-[#3c3c3c] rounded-xl px-4 py-3 focus-within:border-[#007acc]/50 transition-all flex items-center gap-3">
                        <span className="text-[#858585] font-mono text-[10px] font-bold shrink-0">QUERY_CORE:</span>
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="bg-transparent w-full text-[#cccccc] font-mono text-sm placeholder:text-[#858585] focus:outline-none"
                            placeholder="Explore documents, code, or neural nodes..."
                        />
                        <Search className="h-4 w-4 text-[#858585]" />
                    </div>

                    {/* Content Grid */}
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {isLoading ? (
                            <div className="col-span-full py-20 text-center text-neutral-500 font-mono text-xs animate-pulse">
                                SYNCHRONizing_KNOWLEDGE_CORE...
                            </div>
                        ) : filteredItems.length === 0 ? (
                            <div className="col-span-full py-20 text-center text-neutral-500 font-mono text-xs">
                                {searchQuery ? "NO_MATCHES_FOUND" : "KNOWLEDGE_CORE_EMPTY"}
                            </div>
                        ) : filteredItems.map((item, i) => (
                            <motion.div
                                key={item.id}
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: i * 0.05 }}
                                className="group relative p-5 rounded-2xl bg-[#252526] border border-[#303030] hover:bg-[#2d2d2d] hover:border-purple-500/30 transition-all cursor-pointer overflow-hidden"
                            >
                                <div className="absolute top-4 right-4 flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity bg-white/5 p-1 rounded-lg">
                                    <button
                                        onClick={(e) => initiateDelete(e, item.id, item.title)}
                                        className="p-1.5 hover:bg-red-500/20 text-neutral-400 hover:text-red-400 rounded transition-colors"
                                        title="Delete Source"
                                    >
                                        <Trash className="w-3.5 h-3.5" />
                                    </button>
                                    <div className="p-1.5 hover:bg-white/10 text-purple-400 rounded transition-colors">
                                        <ExternalLink className="w-3.5 h-3.5" />
                                    </div>
                                </div>

                                <div className="flex items-start gap-4 mb-4">
                                    <div className="w-10 h-10 rounded-xl bg-purple-500/10 flex items-center justify-center border border-purple-500/20 text-purple-400 group-hover:bg-purple-500/20 transition-colors shrink-0">
                                        <item.icon className="w-5 h-5" />
                                    </div>
                                    <div className="min-w-0 pr-16 text-left">
                                        <h3 className="text-[13px] font-bold text-neutral-200 mb-1 line-clamp-1 group-hover:text-white transition-colors uppercase tracking-tight tracking-wide" title={item.title}>
                                            {item.title}
                                        </h3>
                                        <div className="flex items-center gap-2 text-[10px] font-mono text-neutral-500">
                                            <span className={`px-2 py-0.5 rounded ${item.added === 'READY' ? 'bg-green-500/10 text-green-400 border border-green-500/20' : 'bg-amber-500/10 text-amber-400 border border-amber-500/20'}`}>
                                                {item.added}
                                            </span>
                                            <span className="text-neutral-700">???</span>
                                            <span>{item.type.toUpperCase()}</span>
                                        </div>
                                    </div>
                                </div>

                                <div className="flex items-center justify-between mt-2">
                                    {item.type === 'doc' && (
                                        <div className="text-[10px] text-neutral-500 font-mono bg-white/5 px-2 py-1 rounded inline-block">
                                            SIZE: {item.size}
                                        </div>
                                    )}
                                    {item.type === 'code' && (
                                        <div className="text-[10px] text-green-400/60 font-mono bg-green-500/5 px-2 py-1 rounded inline-block">
                                            CHUNKS: {item.lines}
                                        </div>
                                    )}

                                    {item.added === 'ON_DISK' && (
                                        <button
                                            onClick={(e) => handleIndex(e, item.title)}
                                            className="text-[10px] font-bold font-mono text-purple-400 hover:text-white bg-purple-500/10 hover:bg-purple-500 px-3 py-1 rounded transition-all flex items-center gap-2 border border-purple-500/30"
                                        >
                                            <Zap className="w-3 h-3" /> INDEX_NOW
                                        </button>
                                    )}
                                </div>

                                {/* Card Status Strip */}
                                <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-purple-500/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />
                            </motion.div>
                        ))}

                        {/* Connection Card */}
                        <motion.div
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.3 }}
                            onClick={handleFileClick}
                            className={`p-5 rounded-2xl border border-dashed border-white/5 flex flex-col items-center justify-center gap-3 text-neutral-600 hover:text-purple-400 hover:border-purple-500/40 hover:bg-purple-500/5 transition-all cursor-pointer min-h-[140px] ${isUploading ? 'opacity-50 pointer-events-none' : ''}`}
                        >
                            <div className="w-8 h-8 rounded-full bg-white/5 flex items-center justify-center border border-white/5">
                                {isUploading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Upload className="w-4 h-4" />}
                            </div>
                            <span className="text-[11px] font-mono uppercase tracking-widest font-bold">
                                {isUploading ? "Indexing..." : "Connect_Core_Source"}
                            </span>
                        </motion.div>
                    </div>
                </div>

                {/* Bottom Status Bar ??? VSCode Style */}
                <div className="flex items-center justify-between px-6 py-2 border-t border-[#303030] bg-[#181818] text-[10px] font-mono text-neutral-500 uppercase tracking-widest shrink-0">
                    <div className="flex items-center gap-4">
                        <div className="flex items-center gap-1.5">
                            <div className="w-1 h-1 rounded-full bg-green-500" />
                            <span>KNOWLEDGE_NODE: STABLE</span>
                        </div>
                        <span className="text-neutral-800">|</span>
                        <span>LATENCY: 0.8MS</span>
                    </div>
                    <span>aether.core_stable_v1.0.4</span>
                </div>

            </main>

            {/* Modals */}
            <ConfirmationModal
                isOpen={deleteModal.isOpen}
                onClose={() => setDeleteModal(prev => ({ ...prev, isOpen: false }))}
                onConfirm={confirmDelete}
                title="DELETE_SOURCE_CONFIRMATION"
                message={`Are you sure you want to permanently delete "${deleteModal.filename}"? This action cannot be undone and will remove all associated vector embeddings.`}
                confirmText="DELETE PERMANENTLY"
                isDestructive={true}
            />

            <NotificationModal
                isOpen={notification.isOpen}
                onClose={() => setNotification(prev => ({ ...prev, isOpen: false }))}
                title={notification.title}
                message={notification.message}
                type={notification.type}
            />
        </div>
    );
}

"use client";

import Sidebar from "@/components/Sidebar";
import { Terminal, AlertCircle, CheckCircle2, Info, Hash, Clock, Cpu, Shield, Zap, Search, ChevronRight } from "lucide-react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";

interface Log {
    id: number;
    type: string;
    source: string;
    message: string;
    timestamp: string;
}

export default function AgentLogs() {
    const [currentTime, setCurrentTime] = useState("");
    const [logs, setLogs] = useState<Log[]>([]);
    const [filter, setFilter] = useState("");
    const [activeTab, setActiveTab] = useState("ALL");

    const fetchLogs = async () => {
        try {
            const res = await fetch("http://localhost:8000/logs");
            const data = await res.json();
            if (data.status === "success") {
                setLogs(data.logs);
            }
        } catch (err) {
            console.error("Error fetching logs:", err);
        }
    };

    useEffect(() => {
        fetchLogs();
        const logsInterval = setInterval(fetchLogs, 3000); // Poll every 3 seconds

        const timer = setInterval(() => {
            setCurrentTime(new Date().toLocaleTimeString('en-GB', { hour12: false }));
        }, 1000);
        return () => {
            clearInterval(timer);
            clearInterval(logsInterval);
        };
    }, []);

    const filteredLogs = logs.filter(log => {
        const matchesSearch = log.message.toLowerCase().includes(filter.toLowerCase()) ||
            log.source.toLowerCase().includes(filter.toLowerCase());
        const matchesTab = activeTab === "ALL" || log.source === activeTab;
        return matchesSearch && matchesTab;
    });

    const formatTime = (ts: string) => {
        if (!ts) return "--:--:--";
        try {
            // SQLite timestamp is usually YYYY-MM-DD HH:MM:SS
            const date = new Date(ts.replace(" ", "T"));
            if (isNaN(date.getTime())) return ts;
            return date.toLocaleTimeString('en-GB', { hour12: false }) + "." + String(date.getMilliseconds()).padStart(3, '0');
        } catch {
            return ts;
        }
    };



    return (
        <div className="flex h-screen w-full bg-[#1e1e1e] overflow-hidden font-sans text-foreground">
            <Sidebar />

            <main className="flex-1 min-w-0 flex flex-col relative overflow-hidden z-10 border-l border-[#303030] select-none">

                {/* Header ??? VSCode Style */}
                <div className="px-6 py-4 border-b border-[#303030] flex items-center justify-between bg-[#181818] shrink-0 z-20">
                    <div className="flex items-center gap-3">
                        <div className="w-2 h-2 rounded-full bg-purple-500 animate-pulse" />
                        <div>
                            <h1 className="text-sm font-bold tracking-wider text-white uppercase">System Execution Logs</h1>
                            <div className="flex items-center gap-2 text-[10px] text-neutral-500 font-mono">
                                <span>SYSTEM.KERNEL_LOGS</span>
                                <span className="text-neutral-700">|</span>
                                <span>PID: 8392</span>
                                <span className="text-neutral-700">|</span>
                                <span>TRACE_STATUS: ACTIVE</span>
                            </div>
                        </div>
                    </div>

                    <div className="flex items-center gap-6">
                        <div className="flex gap-3">
                            <div className="flex items-center gap-1.5 px-2.2 py-0.5 rounded border border-red-500/10 bg-red-500/5 text-red-500/80 text-[10px]">
                                <AlertCircle className="w-3 h-3" /> 1_ERR
                            </div>
                            <div className="flex items-center gap-1.5 px-2.2 py-0.5 rounded border border-yellow-500/10 bg-yellow-500/5 text-yellow-500/80 text-[10px]">
                                <AlertCircle className="w-3 h-3" /> 1_WARN
                            </div>
                            <div className="flex items-center gap-1.5 px-2.2 py-0.5 rounded border border-green-500/10 bg-green-500/5 text-green-500 text-[10px]">
                                <CheckCircle2 className="w-3 h-3" /> SYSTEM_OK
                            </div>
                        </div>
                        <div className="text-[10px] text-neutral-600 border-l border-white/10 pl-6 flex items-center gap-2">
                            <Clock className="w-3 h-3" /> {currentTime}
                        </div>
                    </div>
                </div>

                {/* Main Content Area ??? VSCode Editor Style */}
                <div className="flex-1 relative flex flex-col overflow-hidden bg-[#1e1e1e]">

                    {/* Tactical Elements (Removed for clarity/VSCode aesthetic) */}


                    {/* Filter Bar */}
                    <div className="px-6 py-2 border-b border-[#303030] bg-[#252526] flex items-center justify-between z-10">
                        <div className="flex items-center gap-4">
                            <div className="flex items-center gap-2 bg-[#1e1e1e] border border-[#3c3c3c] rounded-md px-2.5 py-1 focus-within:border-[#007acc]/50 transition-all">
                                <Search className="w-3 h-3 text-[#858585]" />
                                <input
                                    type="text"
                                    placeholder="Filter logs..."
                                    value={filter}
                                    onChange={(e) => setFilter(e.target.value)}
                                    className="bg-transparent border-none text-[10px] text-[#cccccc] focus:ring-0 w-32 placeholder:text-[#858585] focus:outline-none"
                                />
                            </div>
                            <div className="flex items-center gap-3">
                                <span className="text-[9px] text-neutral-600 uppercase">Sources:</span>
                                <div className="flex gap-1">
                                    {['ALL', 'CORE', 'MEM', 'NET', 'WEB'].map(tag => (
                                        <button
                                            key={tag}
                                            onClick={() => setActiveTab(tag)}
                                            className={`text-[9px] px-1.5 py-0.5 rounded transition-all ${activeTab === tag ? 'bg-purple-500/20 text-purple-400 border border-purple-500/30' : 'text-neutral-600 hover:text-neutral-400'}`}>
                                            {tag}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        </div>
                        <span className="text-[9px] text-neutral-600">LIVE_TELEMETRY: ON</span>
                    </div>

                    {/* Logs Container */}
                    <div className="flex-1 overflow-y-auto p-6 space-y-1 font-mono text-[11px] relative scrollbar-none">
                        {filteredLogs.map((log, idx) => (
                            <motion.div
                                key={log.id}
                                initial={{ opacity: 0, x: -10 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ delay: Math.min(idx * 0.05, 0.5) }}
                                className="group flex gap-4 py-1 border-l border-white/5 pl-4 hover:border-purple-500/30 hover:bg-white/[0.02] transition-all cursor-crosshair"
                            >
                                <span className="text-neutral-600 w-24 shrink-0 select-none">{formatTime(log.timestamp)}</span>
                                <span className={`w-20 shrink-0 font-bold ${log.type === 'info' ? 'text-blue-500/70' :
                                    log.type === 'success' ? 'text-green-500/70' :
                                        log.type === 'warning' ? 'text-yellow-500/70' : 'text-red-500/70'
                                    }`}>
                                    {log.type.toUpperCase()}
                                </span>
                                <span className="flex-1 text-neutral-400 group-hover:text-neutral-200 transition-colors">
                                    {log.message}
                                </span>
                                <span className="text-[9px] text-neutral-700 uppercase tracking-tighter shrink-0 opacity-0 group-hover:opacity-100 transition-opacity flex items-center gap-1">
                                    <Hash className="w-2.5 h-2.5" /> {log.source}
                                </span>
                            </motion.div>
                        ))}

                        {/* Animated Cursor Entry */}
                        <div className="flex gap-4 py-1 border-l border-white/5 pl-4 opacity-50">
                            <span className="text-neutral-600 w-24 shrink-0">{currentTime}.995</span>
                            <span className="text-blue-500/50 w-20 shrink-0 font-bold uppercase tracking-widest">TRACE</span>
                            <span className="flex items-center gap-1">
                                <span className="text-neutral-500 italic">Listening for system events</span>
                                <motion.span
                                    animate={{ opacity: [1, 0] }}
                                    transition={{ repeat: Infinity, duration: 0.8 }}
                                    className="w-1.5 h-3 bg-purple-500/50"
                                />
                            </span>
                        </div>
                    </div>
                </div>

                {/* Bottom Status Bar ??? VSCode Style */}
                <div className="flex items-center justify-between px-6 py-2 border-t border-[#303030] bg-[#181818] text-[10px] font-mono text-neutral-500 uppercase tracking-widest shrink-0 z-50">
                    <div className="flex items-center gap-4">
                        <div className="flex items-center gap-1.5">
                            <div className="w-1 h-1 rounded-full bg-green-500" />
                            <span>NODE_INFRA: CONNECTED</span>
                        </div>
                        <span className="text-neutral-800">|</span>
                        <div className="flex items-center gap-2">
                            <Cpu className="w-3 h-3 text-neutral-700" />
                            <span>CPU: 12%</span>
                        </div>
                        <span className="text-neutral-800">|</span>
                        <div className="flex items-center gap-2">
                            <Zap className="w-3 h-3 text-neutral-700" />
                            <span>UPTIME: 4H 21M</span>
                        </div>
                    </div>
                    <span>aether.core_stable_v1.0.4</span>
                </div>

            </main>
        </div>
    );
}

"use client";

import { useState, useEffect, useMemo } from "react";
import Sidebar from "@/components/Sidebar";
import { Brain, Network, Clock, Database, X, Trash2, List, Zap, ChevronRight, FileText, Share2 } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export default function Memories() {
    const [memories, setMemories] = useState<any[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [viewMode, setViewMode] = useState<"graph" | "list">("graph");
    const [selectedMemory, setSelectedMemory] = useState<any | null>(null);
    const [conceptGraph, setConceptGraph] = useState<{ nodes: any[], links: any[] }>({ nodes: [], links: [] });
    const [showConstellation, setShowConstellation] = useState(false);
    const [hoveredConcept, setHoveredConcept] = useState<string | null>(null);

    const fetchMemories = async () => {
        try {
            const response = await fetch("http://localhost:8000/memories");
            const data = await response.json();
            if (data.status === "success") {
                setMemories(data.memories);
            }
        } catch (error) {
            console.error("Failed to fetch memories", error);
        } finally {
            setIsLoading(false);
        }
    };

    const deleteMemory = async (id: string) => {
        try {
            const response = await fetch(`http://localhost:8000/memories/${id}`, {
                method: "DELETE"
            });
            const data = await response.json();
            if (data.status === "success") {
                setMemories(prev => prev.filter(m => m.id !== id));
                if (selectedMemory?.id === id) setSelectedMemory(null);
            }
        } catch (error) {
            console.error("Failed to delete memory", error);
        }
    };

    const fetchGraph = async () => {
        try {
            const response = await fetch("http://localhost:8000/graph");
            const data = await response.json();
            if (data.status === "success") {
                setConceptGraph(data.graph);
            }
        } catch (error) {
            console.error("Failed to fetch graph", error);
        }
    };

    useEffect(() => {
        fetchMemories();
        fetchGraph();
    }, []);

    const graphNodes = useMemo(() => {
        return memories.map((mem, index) => {
            const angle = (index / memories.length) * Math.PI * 2;
            const radiusOffset = Math.sin(index * 123.45) * 40;
            const dist = 100 + radiusOffset; // Cluster around Qdrant
            const x = 650 + dist * Math.cos(angle);
            const y = 350 + dist * Math.sin(angle);
            return { ...mem, x, y };
        });
    }, [memories]);

    // Layout logic for the Concept Constellation overlay (Mind Map)
    const constellationLayout = useMemo(() => {
        if (!conceptGraph.nodes.length) return { nodes: [], links: [] };

        const centerX = 500;
        const centerY = 350;

        const nodesWithPos = conceptGraph.nodes.map((node, i) => {
            const angle = (i / conceptGraph.nodes.length) * Math.PI * 2;
            // Push nodes outward in a star pattern, creating visual depth
            const dist = 180 + (i % 3 === 0 ? 80 : i % 2 === 0 ? -40 : 40);
            return {
                ...node,
                x: centerX + Math.cos(angle) * dist,
                y: centerY + Math.sin(angle) * dist,
            };
        });

        const linksWithPos = conceptGraph.links.map(link => {
            const source = nodesWithPos.find(n => n.id === link.source_id);
            const target = nodesWithPos.find(n => n.id === link.target_id);
            return {
                ...link,
                sourceX: source?.x || centerX,
                sourceY: source?.y || centerY,
                targetX: target?.x || centerX,
                targetY: target?.y || centerY,
            };
        });

        return { nodes: nodesWithPos, links: linksWithPos };
    }, [conceptGraph]);

    return (
        <div className="flex h-screen w-full bg-[#1e1e1e] overflow-hidden font-sans text-foreground">
            <Sidebar />

            <main className="flex-1 min-w-0 flex flex-col relative overflow-hidden z-10">
                {/* Header */}
                <div className="px-6 py-4 border-b border-[#303030] flex items-center justify-between bg-[#181818] shrink-0 z-50">
                    <div className="flex items-center gap-3">
                        <div className="w-2 h-2 rounded-full bg-blue-500 animate-pulse" />
                        <div>
                            <h1 className="text-sm font-bold tracking-wider text-white uppercase">Neural Memory Core</h1>
                            <div className="flex items-center gap-2 text-[10px] text-neutral-500 font-mono">
                                <span>SYSTEM.MEM_GRAPH</span>
                                <span className="text-neutral-700">|</span>
                                <span>{memories.length} VECTORS LOADED</span>
                            </div>
                        </div>
                    </div>

                    <div className="flex items-center gap-2 bg-[#252525] p-1 rounded-lg border border-[#303030]">
                        <button
                            onClick={() => setViewMode("graph")}
                            className={`p-1.5 rounded-md transition-colors flex items-center justify-center ${viewMode === "graph" ? "bg-blue-500/20 text-blue-400" : "text-neutral-500 hover:text-neutral-300"}`}
                            title="Graph View"
                        >
                            <Network className="w-4 h-4" />
                        </button>
                        <button
                            onClick={() => setViewMode("list")}
                            className={`p-1.5 rounded-md transition-colors flex items-center justify-center ${viewMode === "list" ? "bg-blue-500/20 text-blue-400" : "text-neutral-500 hover:text-neutral-300"}`}
                            title="List View"
                        >
                            <List className="w-4 h-4" />
                        </button>
                    </div>
                </div>

                {/* Main Content Area */}
                <div className="flex-1 overflow-hidden relative flex">
                    <div className="flex-1 h-full overflow-y-auto bg-[#1e1e1e] relative">
                        {isLoading ? (
                            <div className="flex items-center justify-center h-full">
                                <span className="text-neutral-500 animate-pulse font-mono text-xs flex items-center gap-2">
                                    <Zap className="w-4 h-4" /> Syncing neural nodes...
                                </span>
                            </div>
                        ) : viewMode === "graph" ? (
                            <div className="relative w-full h-full min-h-[600px] overflow-auto flex items-center justify-center bg-[#1e1e1e] cursor-crosshair">

                                {/* Unified Container for SVG and HTML Overlays - Ensures perfect overlap */}
                                <div className="relative w-[1000px] h-[700px] shrink-0">

                                    {/* SVG Layer */}
                                    <svg width="1000" height="700" className="absolute inset-0 pointer-events-none" style={{ filter: "drop-shadow(0 0 15px rgba(59,130,246,0.15))" }}>
                                        <defs>
